import React from 'react'

class SearchBar extends React.Component {
    state = {searchTerm: ""}

    render(){
        return(
            <div>
                <form>
                    <input 
                        type="text" 
                        onChange={(e) => this.setState({searchTerm: e.target.value})} > 
                    </input>
                </form>
            </div>
        )
    }
}

export default SearchBar;
