    var url = 'https://newsapi.org/v2/everything?q=' + item + '&from=2019-01-11&sortBy=publishedAt&apiKey=' + key

    fetch(url)
    .then((response) => {
        return response.json();

    })
    .then((json) => {
        console.log(json)
        this.setState({
            name: json.articles.author,
            title: json.articles.title, 
            description: json.articles.description,
            url: json.articles.url,
            urlToImage: json.articles.urlToImage
        });
    })
    .catch((error) => {
      window.alert("Please enter a vaild article!");
      this.setState({
          name: null,
          title: null,
          desc: null,
          url: null,
      });
  });
}

  render() {
    return (
      <div className="App">
        <header className="App-header">
            <h1>Welcome!</h1>
            <Search 
                handleFormSubmit={(item) => {
                this.handleFormSubmit(item); 
                }}/>

            <CurrentSportsNews
                urlToImage={this.state.urlToImage}
                name={this.state.name}
                title = {this.state.title}
                description= {this.state.description}
                url={this.state.url}
            />


        </header>
      </div>
    );
  }
}



export default App;
